define('text!apps/site-profile/edit/templates/mainview.html',[],function () { return '<header class="aj-imp-dash-header row">\r\n    <div class="aj-imp-dash-title col-xs-12">\r\n        <h2 class="aj-imp-page-head">{{#polyglot}}My Site Profile{{/polyglot}}</h2>\r\n    </div>\r\n</header>\r\n<div class="row">\r\n    <div class="aj-imp-dash-content col-md-12">\r\n        <form class="form-horizontal clearfix" name="form-siteprofile" id="form-siteprofile">\r\n            <div class="scroll-indicator-container scrollsection" id="general-settings" data-text="{{#polyglot}}General Settings{{/polyglot}}">\r\n                <h4 class="aj-imp-sub-head scroll-ref">{{#polyglot}}General Settings{{/polyglot}}</h4>\r\n                <div class="form-group">\r\n                    <label for="inputEmail3" class="col-sm-2 control-label">{{#polyglot}}Site Name{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="input-group">\r\n                            <input type="text" class="form-control" id="sitename" disabled="disabled" name="site_name" \r\n                            value="{{site_domain}}" >\r\n                            <span class="input-group-addon">{{#polyglot}}impruw.com{{/polyglot}}</span>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <label for="domain-name" class="col-sm-2 control-label">{{#polyglot}}Domain Name{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <input type="text" class="form-control" id="domain-name" name="domain_name"\r\n                               value="{{domain_name}}" >\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <a href="#/billing/pricing-plans" class="upgrade" style="display: none">{{#polyglot}}Upgrade your Plan{{/polyglot}}</a>\r\n                        <a href="javascript:void(0)" class="domain-update">{{#polyglot}}Update your Domain Name{{/polyglot}}</a>\r\n                        <span id="msg"></span>\r\n                        <div class="help-block update-help">\r\n                            {{#polyglot}}To ensure your website address is successfully updated in Impruw, you will need to mirror your domain name to www.impruw.com. If you have registered your domain using DreamHost, then login and create Mirror domains via the Manage Domains area of the Web Panel. Click Add New Domain / Sub-Domain and add impruw.com.{{/polyglot}}\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <label for="hotel-name" class="col-sm-2 control-label">{{#polyglot}}Hotel Name{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <input type="text" class="form-control" id="hotel-name" name="hotel_name"\r\n                        value="{{hotel_name}}" >\r\n                    </div>\r\n                </div>\r\n                <div class="form-group">\r\n                    <label class="col-sm-2 control-label"></label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <span class="help-block">\r\n                            {{#polyglot}}This will be your page title. A Page Title is one of the most important on-page ranking factors and should be treated with care. For best results keep your page title less than 70 characters.{{/polyglot}}\r\n                        </span>\r\n                    </div>\r\n                </div>\r\n                <div class="form-group pic-upload">\r\n                    <label for="inputFile3" class="col-sm-2 control-label">{{#polyglot}}Upload Your Logo{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="fileinput fileinput-new" data-provides="fileinput">\r\n                          <div class="fileinput-preview thumbnail" data-trigger="fileinput" style="width: 300px; height: 100px;">\r\n                              <img src ="{{logo_url}}" class="site_profile_images"/>\r\n                          </div>\r\n                          <div>\r\n                            <span class="btn btn-default btn-file">\r\n                                <!--<span class="fileinput-new">Select image</span> -->\r\n                                <span class="fileinput-exists">{{#polyglot}}Change{{/polyglot}}</span>\r\n                                <input type="file" >\r\n                                <input type="text" name="logo_id" id="logo_id" value="{{logo_id}}">\r\n                            </span>\r\n                            <a href="#" class="btn btn-danger fileinput-exists" data-dismiss="fileinput">{{#polyglot}}Remove{{/polyglot}}</a>\r\n                          </div>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n                \r\n                <!--<div class="form-group pic-upload">\r\n                    <label for="inputFile4" class="col-sm-2 control-label">Upload Fav Icon <small>of size 16x16 pixels</small></label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="fileinput fileinput-new" data-provides="fileinput">\r\n                          <div class="fileinput-preview thumbnail" data-trigger="fileinput" style="width: 300px; height: 100px;"></div>\r\n                          <div>\r\n                            <span class="btn btn-default btn-file"><span class="fileinput-new">Select image</span><span class="fileinput-exists">Change</span><input type="file" name="inputFile4"></span>\r\n                            <a href="#" class="btn btn-danger fileinput-exists" data-dismiss="fileinput">Remove</a>\r\n                          </div>\r\n                        </div>\r\n                    </div>\r\n                </div> \r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            Favicon stands for "Favorites Icon". It\'s the little icon beside your site\'s name in the favorites list, before the URL in the address bar and bookmarks folder and as a bookmarked website on the desktop in some operating systems.\r\n                            <h6>Why should you use a favicon?</h6>\r\n                            Most people have a very cluttered bookmarks / favorites list. Having yours stand out with a nice graphical reminder of your site beside it, is a good way to get their attention again.\r\n                        </div>\r\n                    </div>\r\n                </div> -->\r\n            </div>\r\n\r\n            <div class="scroll-indicator-container scrollsection" id="business-details" data-text="{{#polyglot}}Business Details{{/polyglot}}">\r\n                <h4 class="aj-imp-sub-head scroll-ref">{{#polyglot}}Business Details{{/polyglot}}<small>{{#polyglot}}Details for contactus{{/polyglot}}</small></h4>\r\n                    \r\n                <div class="form-group">\r\n                    <label for="inputAddress1" class="col-sm-2 control-label">{{#polyglot}}Street{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <input type="text" value="{{street}}" class="form-control" id="inputAddress1" placeholder="{{#polyglot}}21 Jump Street{{/polyglot}}" name="street">\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group add-another">\r\n                    <label for="inputAddress2" class="col-sm-2 control-label">{{#polyglot}}Postal Code{{/polyglot}}</label>\r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                        <input type="text" class="form-control" name="postal_code" id="inputAddress2" data-mask="999-999" placeholder="{{#polyglot}}420-001{{/polyglot}}" value="{{postal_code}}">\r\n                    </div>\r\n\r\n                    \r\n                    <label for="inputAddress3" class="col-sm-2 control-label label-2">{{#polyglot}}City Town{{/polyglot}}</label>\r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                        <input type="text" class="form-control" id="inputAddress2" placeholder="{{#polyglot}}Gotham City{{/polyglot}}" name="city" value="{{city}}">\r\n                    </div>\r\n                </div>\r\n                \r\n                \r\n                <div class="form-group">\r\n                    <label for="inputAddress4" class="col-sm-2 control-label dd-label">{{#polyglot}}Country{{/polyglot}}</label>\r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                        <select name="country" class="country">\r\n                            <option value="Norway">{{#polyglot}}Norway{{/polyglot}}</option>\r\n                            <option value="Sweden">{{#polyglot}}Sweden{{/polyglot}}</option>\r\n                            <option value="Denmark">{{#polyglot}}Denmark{{/polyglot}}</option>\r\n                            <option value="India">{{#polyglot}}India{{/polyglot}}</option>\r\n                            <option value="UK">{{#polyglot}}UK{{/polyglot}}</option>\r\n                        </select>\r\n                    </div>\r\n                </div>\r\n                \r\n                \r\n                <div class="form-group add-another">\r\n                    <label for="inputEmail1" class="col-sm-2 control-label">{{#polyglot}}Email{{/polyglot}}</label>\r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                        <input type="email" class="form-control" id="inputEmail1" placeholder="{{#polyglot}}youremail@site.com{{/polyglot}}" name="site_email" value="{{site_email}}">\r\n                        <!--<span class="help-block add-another-link"><a href="#"><span class="glyphicon glyphicon-plus-sign"></span> Add Another</a></span>-->\r\n                    </div>                              \r\n                \r\n                    <label for="inputPhone1" class="col-sm-2 control-label label-2">{{#polyglot}}Phone{{/polyglot}}</label>\r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                        <input type="text" class="form-control" id="inputEmail1" data-mask="99-999-999" placeholder="{{#polyglot}}99-888-777{{/polyglot}}" name="other_phone_no[]" value="{{other_phone_no}}">\r\n                        <!--<span class="help-block add-another-link"><a href="#"><span class="glyphicon glyphicon-plus-sign"></span> Add Another</a></span>-->\r\n                    </div>\r\n                </div>\r\n\r\n               <!-- <div class="form-group add-another">\r\n    \r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                            <span class="div_email"><input type="email" class="form-control" name="email[]" placeholder="youremail@site.com" value="myemail@asd.com">  <span class="del_email" style="display:none;">Delete</span> </span>                       \r\n                        <span class="help-block add-another-link"><a href="#" id="add_another_email"><span class="glyphicon glyphicon-plus-sign"></span> Add Another</a></span>\r\n                    </div>                                \r\n\r\n                    \r\n                    <div class="col-sm-4 col-sm-offset-2">\r\n                      \r\n                        <span class="div_phone"><input type="text" class="form-control" name="phone[]" data-mask="99-999-999" placeholder="99-888-777" value="99999999999999"> <span class="del_phone" style="display: none">Delete</span> </span>                                                                   \r\n                    \r\n                        <span class="help-block add-another-link"><a href="#" id="add_another_phone"><span class="glyphicon glyphicon-plus-sign"></span> Add Another</a></span>\r\n                    </div>\r\n                </div>\r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            Email accounts and phone numbers are easy to setup and there is really no limit to how many you can have. Use this to better organize and manage your communication.\r\n                        </div>\r\n                    </div>\r\n                </div> -->\r\n            </div>\r\n\r\n            <div class="scroll-indicator-container scrollsection" id="social-settings" data-text="{{#polyglot}}Social Settings{{/polyglot}}">\r\n                <h4 class="aj-imp-sub-head scroll-ref">{{#polyglot}}Social Settings{{/polyglot}}<small>{{#polyglot}}Update social tags{{/polyglot}}</small></h4>\r\n                <div class="form-group">\r\n                    <label for="inputSocial1" class="col-sm-2 control-label">{{#polyglot}}Facebook{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="input-group">\r\n                            <span class="input-group-addon">https://facebook.com/</span>\r\n                            <input type="text" class="form-control" id="inputSocial1" placeholder="{{#polyglot}}Only enter your Facebook page name{{/polyglot}}" name="facebook" value="{{facebook}}">\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group">\r\n                    <label for="inputSocial2" class="col-sm-2 control-label">{{#polyglot}}Twitter{{/polyglot}}</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="input-group">\r\n                            <span class="input-group-addon">https://twitter.com/</span>\r\n                            <input type="text" class="form-control" id="inputSocial2" placeholder="{{#polyglot}}Only enter your Twitter username{{/polyglot}}" name="twitter" value ="{{twitter}}">\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            {{#polyglot}}Good social links{{/polyglot}}\r\n                            <h6>{{#polyglot}}The future{{/polyglot}}</h6>\r\n                            {{#polyglot}}social media marketing{{/polyglot}}\r\n                            <br><br>\r\n                            <h6>{{#polyglot}}search results{{/polyglot}}</h6>\r\n                            {{#polyglot}}search engine{{/polyglot}}\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!--<div class="scroll-indicator-container" id="scr4">\r\n                <h4 class="aj-imp-sub-head scroll-ref">SEO <small>Make your website rank higher</small></h4>\r\n                <div class="form-group">\r\n                    <label for="inputSEO1" class="col-sm-2 control-label">Site Description</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <textarea class="form-control" rows="3" name="inputSEO1" placeholder="A brief description of your site for search engines."></textarea>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            Keep site descriptions between 150 and 160 characters as search engines generally truncate snippets longer than 160 characters.\r\n                            <h6>What is a site description?</h6>\r\n                            The site description serves the function of advertising copy. It draws readers to a website from the search engines and thus, is an extremely important part of search marketing. Crafting a readable, compelling description using important keywords can improve the click-through rate for a given webpage. To maximize click-through rates on search engine result pages, it\'s important to note that Google and other search engines bold keywords in the description when they match search queries.\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <label for="inputSEO2" class="col-sm-2 control-label">Meta Keywords</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <textarea class="form-control" rows="3" name="inputSEO2" placeholder="Separate each keyword with a comma."></textarea>\r\n                    </div>\r\n                </div>\r\n\r\n                <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            A meta keywords tag is supposed to be a brief and concise list of the most important terms on your page.\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group">\r\n                    <label for="inputSEO3" class="col-sm-2 control-label">Footer Code</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <textarea class="form-control" rows="3" name="inputSEO3" placeholder="ex. Google Analytics tracking code."></textarea>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group">\r\n                    <label for="inputSEO4" class="col-sm-2 control-label">Header Code</label>\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <textarea class="form-control" rows="3" name="inputSEO4" placeholder="ex. Google Webmaster tools verification code."></textarea>\r\n                    </div>\r\n                </div>\r\n\r\n                 <div class="form-group">\r\n                    <div class="col-sm-10 col-sm-offset-2">\r\n                        <div class="help-block">\r\n                            <h6>Why is it important to gauge my website metrics?</h6>\r\n                            Knowing precisely what your website visitors -- who are often current and potential customers -- like and dislike about your site can help you reach your overall business goals.\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class="form-group">\r\n                    <div class="col-sm-offset-2 col-sm-10">\r\n                        <label for="checkbox2" class="checkbox checked">\r\n                            <input type="checkbox" data-toggle="checkbox" checked="checked" id="checkbox2" value="">\r\n                            Hide site from Search Engines\r\n                        </label>\r\n                        <div class="help-block">\r\n                            If you choose this your website will no longer be available on the search results.\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div> \r\n\r\n            <div class="scroll-indicator-container" id="scr5">\r\n                <h4 class="aj-imp-sub-head scroll-ref">Editors <small>Select people that are allowed to edit your website.</small></h4>\r\n                <table class="table table-striped table-bordered table-hover">\r\n                    <thead>\r\n                        <tr>\r\n                            <th>Name</th>\r\n                            <th>Role</th>\r\n                            <th>Last Login</th>\r\n                            <th>Actions</th>\r\n                        </tr>\r\n                    </thead>\r\n                    <tbody data-link="row" class="rowlink">\r\n                        <tr>\r\n                            <td>\r\n                                <a href="#rowlinkModal" data-toggle="modal">admin@impruw.com</a>\r\n                            </td>\r\n                            <td>\r\n                                Admin\r\n                            </td>\r\n                            <td>\r\n                                23/11/2013 @ 13:09\r\n                            </td>\r\n                            <td class="rowlink-skip">\r\n                                <a href="#">Delete</a>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <a href="#rowlinkModal" data-toggle="modal">user@impruw.com</a>\r\n                            </td>\r\n                            <td>\r\n                                Author\r\n                            </td>\r\n                            <td>\r\n                                23/11/2013 @ 13:09\r\n                            </td>\r\n                            <td class="rowlink-skip">\r\n                                <a href="#">Delete</a>\r\n                            </td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td>\r\n                                <a href="#rowlinkModal" data-toggle="modal">user@impruw.com</a>\r\n                            </td>\r\n                            <td>\r\n                                Author\r\n                            </td>\r\n                            <td>\r\n                                23/11/2013 @ 13:09\r\n                            </td>\r\n                            <td class="rowlink-skip">\r\n                                <a href="#">Delete</a>\r\n                            </td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n                <a href="#addeditorModal" data-toggle="modal" style="display: block; margin: 0 0 1em;"><span class="glyphicon glyphicon-plus-sign"></span> Add Editor</a>\r\n\r\n                <div class="form-group">\r\n                    <div class="col-sm-12">\r\n                        <div class="help-block">\r\n                            <h5>Roles and their Capabilities</h5>\r\n                            <h6>A user can have one of the below given roles.</h6>\r\n                            <a href="http://codex.wordpress.org/Roles_and_Capabilities#Administrator" target="_blank">Administrator</a> â€“ somebody who has access to all the administration features in the website.<br>\r\n                            <a href="http://codex.wordpress.org/Roles_and_Capabilities#Editor" target="_blank">Editor</a> â€“ somebody who can publish and manage posts including the posts of other users.<br>\r\n                            <a href="http://codex.wordpress.org/Roles_and_Capabilities#Author" target="_blank">Author</a> â€“ somebody who can publish and manage their own posts.\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div> -->\r\n\r\n\r\n            <div class="aj-imp-long-form-actions" data-spy="affix">\r\n                <div class="action">\r\n                    <!--<div class="affix-show">You have unsaved changes!</div>-->\r\n                    <button type="button" id="btn_savesitedetails"  name="btn_savesitedetails" class="btn btn-default btn-wide aj-imp-submit">{{#polyglot}}Save{{/polyglot}}</button>\r\n                    <img src =\'{{THEMEURL}}/images/loader.gif\' width="38" height="30"  id="siteprofilesubmitm_loader" style="display:none"/>\r\n                </div>\r\n            </div>\r\n            \r\n        </form>\r\n        \r\n    </div>\r\n</div>\r\n\r\n\r\n\r\n\r\n';});


